import React, { Component } from 'react';
import { Button, Container, Input} from 'reactstrap';
let temp = 0;
class Query extends Component {
    constructor() {
        super();
        this.state = {
            UserName: '',
            UserEmail: '',
            UserNumber: ''
        }
        this.UserName = this.UserName.bind(this);
        this.UserEmail = this.UserEmail.bind(this);
        this.UserNumber = this.UserNumber.bind(this);
        this.getQuote = this.getQuote.bind(this);
    }
    UserName(event) {
        this.setState({ UserName: event.target.value })
    }
    UserEmail(event) {
        this.setState({ UserEmail: event.target.value })
    }
    UserNumber(event) {
        this.setState({ UserNumber: event.target.value })
    }
    getQuote(event) {
        var forms = document.getElementsByClassName('needs-validation');
        Array.prototype.filter.call(forms, function (form) {
            form.addEventListener('submit', function (event) {
                if (form.checkValidity() === false) {
                    event.preventDefault();
                    event.stopPropagation();
                }
                temp = 1;
                form.classList.add('was-validated');
            }, false);
        });
        console.log(temp);
        debugger;
        if (temp === 1) {
            debugger;
            this.props.history.push("/Dashboard");
        }

    }
    render() {
        return (
            <div className="app flex-row align-items-center">


                <div className="list-group-header section-header row">
                            <div className="col">
                                <span className="mb-1 underline">Send New </span>
                                <span className="mb-1 blue-color pl-2">Quote Request</span>
                            </div>
                            <div className="col text-right">
                                <button type="button" class="btn btn-primary btn-sm pr-4 pl-4">Reset</button>
                                <button type="button" class="btn btn-secondary btn-sm ml-2 pr-4 pl-4">Send</button>
                            </div>
                        </div>


                <div className="quote-req">
                    <form className="needs-validation">

                        <div class="form-group">
                            <label for="description">Description</label>
                            <textarea class="form-control" id="description" rows="3"></textarea>
                        </div>


                        <Button onClick={this.getQuote} type="submit" className="btn btn-primary" block>Get a quote</Button>

                    </form>
                </div>
            </div>
        );
    }
}
export default Query;
