import React, { Component } from 'react';

class EditInventory extends Component {
    state = {
        item : this.props.selectedItem
    }
    handleChange(propertyName, event) {
        var item = this.state.item;
        item[propertyName] = event.target.value;
        this.setState({ item: item });
    }
    saveInventory() {
        console.log(this.state.item);
    }
    resetReq() {

    }
    contactSupplier() {

    }
    render() {
        return (
            <React.Fragment>

                <div className="col admin-quote-page">
                    <div className="list-group-header section-header row">
                        <div className="col-4">
                            <span className="mb-1 underline">Manage</span>
                            <span className="mb-1 blue-color pl-2">Inventory</span>

                            <span className="mr-2 ml-2">&#62;</span>

                            <span className="mb-1">Edit</span>
                            <span className="mb-1 blue-color pl-2">Inventory</span>
                        </div>
                        <div className="col-8 text-right">
                            <button type="button" className="btn btn-blue btn-sm pr-4 pl-4" onClick={() => this.resetReq()} >Reset</button>
                            <button type="button" className="btn btn-green btn-sm ml-2 pr-4 pl-4" onClick= {() => this.saveInventory()}>Save</button>
                        </div>
                    </div>

                    <div>
                        <div className="quote-req row">
                            <div className="col">
                                <div>
                                    <span>Item Name</span>
                                    <input type="text"
                                    className="form-control" defaultValue={this.state.item.itemName} 
                                    onChange={this.handleChange.bind(this, 'itemName')} />
                                </div>
                                <div>
                                    <span>Description</span>
                                    <textarea className="form-control" rows="3" 
                                    defaultValue={this.state.item.description}
                                    onChange={this.handleChange.bind(this, 'description')}></textarea>
                                </div>
                                <div>
                                    <span>Availability</span>
                                    <div >
                                        <input type="number" 
                                        className="form-control d-inline" defaultValue={this.state.item.availability} 
                                        onChange={this.handleChange.bind(this, 'availability')} />
                                        <button type="button" className="btn btn-blue btn-sm pr-4 pl-4 d-inline" onClick={() => this.contactSupplier()} >Contact supplier</button>
                                    </div>

                                </div>
                                <div>
                                    <span>Cost</span>
                                    <input type="number" 
                                    className="form-control" defaultValue={this.state.item.cost} 
                                    onChange={this.handleChange.bind(this, 'cost')} />
                                </div>
                            </div>
                            <div className="col">
                                <span>Operatios Tagged</span>
                                <div>
                                    {this.state.item.operationsTaged.map(operation => (
                                        <span className="badge tool-badge">Operation - {operation}</span>
                                    )
                                    )}
                                </div>
                            </div>

                        </div>

                    </div>

                </div>


            </React.Fragment>
        );
    }
}
export default EditInventory;
