import axios from "axios";

const API_URL = "https://funetus-api.herokuapp.com/auth/";

class AuthService {
  login(data) {

    var config = {
        method: 'post',
        url: API_URL+'login',
        headers: { 
          'Content-Type': 'application/json'
        },
        data : data
      };

      axios(config)
      .then(function (response) {
        console.log(JSON.stringify(response.data));  
      })
      .catch(function (error) {
        console.log(error);
      });





/*
