import React, { useState } from "react";

function Quote() {
    const [inputList, setInputList] = useState([{ firstName: "", lastName: "" }]);
    const [formInputList,setFormInputList] = useState([{
        "data0": [{
                "name": "Item Name",
                "type": "text",
                "value": 7
            }, {
                "name": "Unit",
                "type": "text",
                "value": 2
            },
            {
                "name": "Quantity ",
                "type": "text",
                "value": 1
            }
        ]
    }]);
    /*const formInputList = [{
        "data0": [{
                "name": "Item Name",
                "type": "text",
                "value": 7
            }, {
                "name": "Unit",
                "type": "text",
                "value": 2
            },
            {
                "name": "Quantity ",
                "type": "text",
                "value": 1
            }
        ]
    }];
    const newKey ="data"+formInputList.length; 
    const newFormInputList = {
        [newKey]: [{
                "name": "Item Name22",
                "type": "text",
                "value": 7777
            }, {
                "name": "Unit22",
                "type": "text",
                "value": 2222
            },
            {
                "name": "Quantity22 ",
                "type": "text",
                "value": 11111
            }
        ]
    }
    formInputList.push(newFormInputList);
    console.log("formInputList:",formInputList);
*/
    // handle input change
    const handleInputChange = (e, index) => {
        const { name, value } = e.target;
        const list = [...inputList];
        list[index][name] = value;
        setInputList(list);
    };

    // handle click event of the Remove button
    const handleRemoveClick = index => {
        debugger
        /*const list = [...inputList];
        list.splice(index, 1);
        setInputList(list);*/

        const newList = [...formInputList];
        newList.splice(index, 1);
        setFormInputList(newList);




    };

    // handle click event of the Add button
    const handleAddClick = () => {
        //setInputList([...inputList, { firstName: "", lastName: "" }]);

        const newKey ="data"+formInputList.length; 
        const newFormInputList = {
        [newKey]: [{
                "name": "Item Name22",
                "type": "text",
                "value": ''
            }, {
                "name": "Unit22",
                "type": "text",
                "value": ''
            },
            {
                "name": "Quantity22 ",
                "type": "text",
                "value": ''
            }
        ]
    }
        setFormInputList([...formInputList, newFormInputList]);
    };

    return (
        <div className="app flex-row align-items-center">
            <div className="list-group-header section-header row">
                <div className="col">
                    <span className="mb-1 underline">Send New </span>
                    <span className="mb-1 blue-color pl-2">Quote Request</span>
                </div>
                <div className="col text-right">
                    <button type="button" class="btn btn-primary btn-sm pr-4 pl-4">Reset</button>
                    <button type="button" class="btn btn-secondary btn-sm ml-2 pr-4 pl-4">Send</button>
                </div>
            </div>
            <div className="quote-req">


                <div class="form-group">
                    <label for="title">Title</label>
                    <input type="text" class="form-control" id="title" />
                </div>

                <div class="form-group">
                    <label for="description">Description</label>
                    <textarea class="form-control" id="description" rows="3"></textarea>
                </div>


                <div class="form-group">

                    <label>Measurements</label>
                    <button class="btn btn-primary btn-sm pr-4 pl-4" onClick={handleAddClick}>Add</button>

                    {inputList.map((x, i) => {
                        return (
                            <div className="row mt-3">
                                <div className="col-sm">
                                <input
                                    class="form-control"
                                    name="firstName"
                                    placeholder="Enter First Name"
                                    value={x.firstName}
                                    onChange={e => handleInputChange(e, i)}
                                />
                                </div>
                                <div className="col-sm">
                                <input
                                    class="form-control"
                                    name="lastName"
                                    placeholder="Enter Last Name"
                                    value={x.lastName}
                                    onChange={e => handleInputChange(e, i)}
                                />
                                </div>
                                <div className="col-sm">
                                <div className="btn-box">
                                    <button
                                        class="btn btn-primary btn-sm pr-4 pl-4"
                                        onClick={() => handleRemoveClick(i)}>Remove</button>

                                </div>
                                </div>
                            </div>
                        );
                    })}
                </div>



                <div>
                    <label>Attachments</label>
                    <button type="button" class="btn btn-primary btn-sm pr-4 pl-4">Browse</button>
                </div>




    <div>
    {formInputList.map((x, i) => {

        const rowKey = "data"+i;
         return (
           <div className="row">

              {formInputList[i][rowKey].map((dataItem) => {
                return (
                    <div className="col-sm">
                        <span>{dataItem.name}</span>  
                        <input
                                    class="form-control"
                                    name={dataItem.name}
                                    placeholder={dataItem.name}
                                    value={dataItem.value}

                                />


                    </div>                 


                )
               })}
               <div className="col-sm">
                                <div className="btn-box">
                                    <button
                                        class="btn btn-primary btn-sm pr-4 pl-4"
                                        onClick={() => handleRemoveClick(i)}>Remove</button>

                                </div>
                                </div>
           </div>
         )


       })}
     </div>



                {formInputList.map((x, i) => {


                    const rowKey = "data"+i;

                    const data = x[rowKey];

                   {data.map((y,j) => {

                    return (

                        <div className="row mt-3">
                            <div className="col-sm">
                            <label>{y.name}</label>
                            </div>

                        </div>
                    );

                    })
                   } 






                            /*return (

                                <div className="row mt-3">
                                    <div className="col-sm">
                                    <label>{i}</label>
                                    </div>

                                </div>
                            );*/








                    })}















            </div>



        </div>
    );
}

export default Quote;
