import React, { Component } from 'react';


class Quote extends Component {


    constructor(props) {
        super(props);
        this.state = {
            //rowList: [true]
            //rowList: [{"id":0}]
            rowList: [{
                "rowId": 0,
                "data": [{
                    "itemName": "name",
                    "itemType": "textbox",
                    "itemValue": ""
                }, {
                    "itemName": "unit",
                    "itemType": "textbox",
                    "itemValue": ""
                }, {
                    "itemName": "quantity",
                    "itemType": "textbox",
                    "itemValue": ""
                }]
            }]
        }
        /* "recipes": {

            "1": {
              "id": 1,
              "name": "Pumpkin Pie",
              "ingredients": [
                "Pumpkin Puree",
                "Sweetened Condensed Milk",
                "Eggs",
                "Pumpkin Pie Spice",
                "Pie Crust"
              ]
            }

          }*/

        this.state = {
            items: [
                { id: 1, name: "item 1" },
                { id: 2, name: "item 2" },
                { id: 3, name: "item 3" }
              ]
        };
        this.jsonFormData = [  
            {  
                "indexId":"1",
                "indexName":"itemname",
                "indexDesc":"Item Name",
                "htmlControlType":"textbox",
                "cssClassName":"form-control"
            },
            {  
                "indexId":"2",
                "indexName":"unit",
                "indexDesc":"Unit",
                "htmlControlType":"textbox",
                "cssClassName":"form-control"
            },
            {  
                "indexId":"3",
                "indexName":"address",
                "indexDesc":"Quantity",
                "htmlControlType":"textbox",
                "cssClassName":"form-control"
        }];

        this.renderRecipes = this.renderRecipes.bind(this);
        this.deleteRecipe = this.deleteRecipe.bind(this);
      }

deleteRecipe(e) {
    debugger;
    const itemId = e.currentTarget.dataset.id;

    const filteredItems = this.state.items.filter(item => item.id !== 1);
    this.setState({ filteredItems });
     /* this.setState(state => {
        const { items } = state;
        const filteredItems = items.filter(item => item.id !== itemId);
        return {
          items: filteredItems
        };
      });*/


    /*const recipes = Object.assign({}, this.state.recipes);
    const id = e.currentTarget.dataset.id;
    delete recipes[id];
    this.setState({ recipes });*/
  }

  renderRecipes() {
      debugger;
    const items = [];

    return this.state.items.map(group => {
        return <div className="col-sm">

                <input type="text"
                         className="form-control"
                         id={group.name}
                         placeholder="" />
                <button type="button" data-id={group.id} onClick={this.deleteRecipe}>remove</button>
                {this.state.items.name}
        </div>
    });



  }





    add() {
        debugger;

        this.setState(state => {
            const { items } = state;
            const newId = 8;
            const newItem = { id: newId, text: `item ${newId}` };
            return {
              items: [...items, newItem]
            };
          });

        console.log(this.state.items);

       /* let rowList = [...this.state.rowList];

        let newRow = {
            "rowId": rowList.length,
            "data": [{
                "itemName": "name",
                "itemType": "textbox",
                "itemValue": ""
            }, {
                "itemName": "unit",
                "itemType": "textbox",
                "itemValue": ""
            }, {
                "itemName": "quantity",
                "itemType": "textbox",
                "itemValue": ""
            }]
        }


        rowList.push(newRow);
        console.log(rowList);
        this.setState({ rowList }); */
      }

      remove(i) {
        let removedList = this.state.rowList;
        removedList.splice(i,1);
        this.setState({rowList: removedList});

        debugger;
      }
      onInputchange(i,k,event) {

          this.state.rowList[i].data[k].itemValue = event.target.value;
       /* this.setState({
            [this.state.rowList[i].data[k].itemValue]: event.target.value
          });*/

      }

    addMeasurementRow(data) {
        return data.map(group => {
            return <div className="col-sm">

                    <input type="text"
                             className="form-control"
                             id={group.indexName}
                             placeholder="" />
            </div>
        });
    }

    renderFormGroups(data) {


        return data.map(group => {
            return <div className="col-sm">

                <label for={group.indexName}
                       className={""}>
                    {group.indexDesc}
                </label>
            </div>
        });
    }


    render() {
        let { rowList } = this.state;
        return (















            <div className="app flex-row align-items-center">


                <div className="list-group-header section-header row">
                    <div className="col">
                        <span className="mb-1 underline">Send New </span>
                        <span className="mb-1 blue-color pl-2">Quote Request</span>
                    </div>
                    <div className="col text-right">
                        <button type="button" class="btn btn-primary btn-sm pr-4 pl-4">Reset</button>
                        <button type="button" class="btn btn-secondary btn-sm ml-2 pr-4 pl-4">Send</button>
                   </div>
                </div>


                <div className="quote-req">
                    <form className="needs-validation">

                        <div class="form-group">
                            <label for="title">Title</label>
                            <input type="text" class="form-control" id="title" />
                        </div>

                        <div class="form-group">
                            <label for="description">Description</label>
                            <textarea class="form-control" id="description" rows="3"></textarea>
                        </div>


                        <div class="form-group">





  <div class="row">
{this.addMeasurementRow(this.jsonFormData)}
    </div>



                        </div>






                    </form>




<div className="intent" id="intent1">

    <label for="measurements">Measurements</label>
    <button onClick={() => this.add()}>Add New</button>


<div>
        {this.renderRecipes()}
      </div>





</div>















            </div>


            </div>



        );
    }
}
export default Quote;
