import React, { Component } from 'react';
import inventoryJson from '../../data/inventoryData.json';

import EditInventory from './EditInventory';

class ManageInventory extends Component {
    state = {
        searchValue: "",
        listitems: inventoryJson.inventoryList,
        selectedItem: [],
        editInventoryPage: false
    }
    editInventory() {
        this.setState({
            editInventoryPage: true
        });
    }
    deleteInventory() {

    }
    onInventorySelected(selectedItem) {
        this.setState({
            selectedItem: selectedItem
        });
    }
    parentCallback() {
        this.setState({
            editInventoryPage:false
          });
    }
    renderInventoryList() {
        return (<div className="col admin-quote-page">
            <div className="list-group-header section-header row">
                <div className="col-4">
                    <span className="mb-1 underline">Manage</span>
                    <span className="mb-1 blue-color pl-2">Inventory</span>
                </div>
                <div className="col-8 text-right">
                    <div className="has-search">
                        <span className="fa fa-search form-control-feedback"></span>
                        <input type="text" className="form-control search-box" placeholder="Search quote requests..." />
                    </div>
                    <button className="btn delete-btn" onClick={() => this.deleteInventory()}></button>
                    <button className="btn edit-btn" onClick={() => this.editInventory()}></button>
                    <button className="btn add-btn"></button>
                </div>
            </div>
            <div className="quote-req-list">
                <div className="row mt-1 quote-req-header">
                    <div className="col-sm">
                        <label>Item Name</label>
                    </div>
                    <div className="col-sm">
                        <label>Description</label>
                    </div>
                    <div className="col-sm">
                        <label>Availability</label>
                    </div>
                    <div className="col-sm">
                        <label>Operations Taged</label>
                    </div>
                    <div className="col-sm">
                        <label>Cost</label>
                    </div>
                    <div className="col-sm">
                        <label>Created On</label>
                    </div>
                    <div className="col-sm">
                        <label>Modified On</label>
                    </div>
                </div>
                <div className="quote-req-table">
                    {this.state.listitems.map(item => (
                        <div className="row mt-1" key={item.id}>
                            <div className="col-sm" >
                                <label className="btn btn-default blue">
                                    <input type="radio" className="toggle"
                                        name="quoteItem" value={item.id}
                                        onChange={() => this.onInventorySelected(item)} />
                                </label>
                                <label>{item.itemName}</label>
                            </div>
                            <div className="col-sm" >
                                <label className="description-truncate text-truncate">{item.description}</label>
                            </div>
                            <div className="col-sm" >
                                <label>{item.availability}</label>
                            </div>
                            <div className="operation-div col-sm" >
                                <label className="operation-text-truncate">{item.operationsTaged}</label>
                                <span className="badge badge-light float-right">+{item.operationsTaged.length}</span>
                            </div>
                            <div className="col-sm" >
                                <label>{item.cost}</label>
                            </div>
                            <div className="col-sm" >
                                <label>{item.createdOn}</label>
                            </div>
                            <div className="col-sm" >
                                <label>{item.modifiedOn}</label>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>);
    }
    render() {
        return (
            <React.Fragment>
                {this.state.editInventoryPage ? <EditInventory selectedItem={this.state.selectedItem} parentCallback= {this.parentCallback}/> : this.renderInventoryList()}
            </React.Fragment>
        );
    }
}
export default ManageInventory;
