import React, { Component } from 'react';

class EditInventory extends Component {
    state = {
        item : this.props.selectedItem
    }
    handleChange(propertyName, event) {
        var item = this.state.item;
        item[propertyName] = event.target.value;
        this.setState({ item: item });
    }
    saveInventory() {
        console.log(this.state.item);
    }
    resetReq() {

    }
    render() {
        return (
            <React.Fragment>

                <div className="col admin-quote-page">
                    <div className="list-group-header section-header row">
                        <div className="col-4">
                            <span className="mb-1 underline">Manage</span>
                            <span className="mb-1 blue-color pl-2">Inventory</span>

                            <span className="mr-2 ml-2">&#62;</span>

                            <span className="mb-1">Edit</span>
                            <span className="mb-1 blue-color pl-2">Inventory</span>
                        </div>
                        <div className="col-8 text-right">
                            <button type="button" class="btn btn-blue btn-sm pr-4 pl-4" onClick={() => this.resetReq()} >Reset</button>
                            <button type="button" class="btn btn-green btn-sm ml-2 pr-4 pl-4" onClick= {() => this.saveInventory()}>Save</button>
                        </div>
                    </div>

                    <div>
                        <div className="quote-req row">
                            <div className="col">
                                <div>
                                    <span>Item Name</span>
                                    <input type="text"
                                    class="form-control" defaultValue={this.state.item.itemName} 
                                    onChange={this.handleChange.bind(this, 'itemName')} />
                                </div>
                                <div>
                                    <span>Description</span>
                                    <textarea class="form-control" rows="3" onChange={this.handleChange.bind(this, 'description')}></textarea>
                                </div>
                                <div>
                                    <span>Availability</span>
                                    <input type="text" 
                                    class="form-control" defaultValue={this.state.item.availability} 
                                    onChange={this.handleChange.bind(this, 'availability')} />
                                </div>
                                <div>
                                    <span>Cost</span>
                                    <input type="number" 
                                    class="form-control" defaultValue={this.state.item.cost} 
                                    onChange={this.handleChange.bind(this, 'cost')} />
                                </div>
                            </div>
                            <div className="col">
                                <div>
                                    <span>Operatios Tagged</span>
                                </div>
                            </div>

                        </div>

                    </div>

                </div>


            </React.Fragment>
        );
    }
}
export default EditInventory;
